import React, { Component } from 'react'
import { getList, addToList, deleteItem, updateItem } from './TodoListFunctions'
import FormValidator from './FormValidator'
import Stoptimer from './StopTimer'

export class TodoList extends Component {

  constructor() {
    super()
    this.validator = new FormValidator([
      {
        field: 'task',
        method: 'isEmpty',
        validWhen: false,
        message: 'Task name is required'
      },
      {
        field: 'description',
        method: 'isEmpty',
        validWhen: false,
        message: 'Description is required'
      }
    ]);

    this.state = {
      id: '',
      task: '',
      description: '',
      createdAt: '',
      timeTask: '',
      isUpdate: false,
      errorMessage: '',
      items: [],
      validation: this.validator.valid(),
      timerOn: false,
      timerStart: 0,
      timerTime: 0
    }
    this.onSubmit = this.onSubmit.bind(this)
    this.onChange = this.onChange.bind(this)
    this.onCreate = this.onCreate.bind(this)
  }

  componentDidMount() {
    this.getAll()
  }

  onChange(e) {
    this.setState({ [e.target.name]: e.target.value })
  }

  onCreate(e) {
    e.preventDefault()
    this.setState({
      id: '',
      task: '',
      description: '',
      createdAt: '',
      timeTask: '',
      isUpdate: false,
      errorMessage: '',
      items: [],
      validation: this.validator.valid(),
    });
    this.getAll()
  }

  formatDate(date) {
    var monthNames = [
      "January", "February", "March",
      "April", "May", "June", "July",
      "August", "September", "October",
      "November", "December"
    ];
    return date.getDate() + ' ' + monthNames[date.getMonth()] + ', ' + date.getFullYear();
  }

  formatTime(time) {
    return time.getHours()+ ':' + time.getMinutes() + ':' + time.getSeconds();
  }

  onUpdateTime = (time) => {
    const validation = this.validator.validate(this.state);
    this.setState({ validation });
      const taskUpdateRequest = {
        id: this.state.id,
        description: time
      }
      updateItem(taskUpdateRequest).then(() => {
        this.getAll()
      }).catch(err => {
        this.setState({ editDisabled: false, isUpdate: false, errorMessage: err.message })
      })
    this.setState({ editDisabled: false, isUpdate: false})
  }

  getAll = () => {
    getList().then(data => {
      this.setState(
        {
          task: '',
          items: [...data]
        },
        () => {
          console.log(this.state.items)
        }
      )
    })
  }

  onSubmit = e => {
    e.preventDefault()
    const validation = this.validator.validate(this.state);
    this.setState({ validation });
    if (validation.isValid) {

      const taskRequest = {
        name: this.state.task,
        description: this.state.description
      }

      addToList(taskRequest).then(() => {
        this.getAll()
      }).catch(err => {
        this.setState({ editDisabled: false, errorMessage: err.message })
      })
      this.setState({ editDisabled: false })
    }
  }


  onUpdate = e => {
    e.preventDefault()
    const validation = this.validator.validate(this.state);
    this.setState({ validation });
    if (validation.isValid) {
      const taskUpdateRequest = {
        id: this.state.id,
        name: this.state.task,
        description: this.state.description
      }
      updateItem(taskUpdateRequest).then(() => {
        this.getAll()
      }).catch(err => {
        this.setState({ editDisabled: false, isUpdate: false, errorMessage: err.message })
      })
    }
    this.setState({ editDisabled: false, isUpdate: false})
  }

  onEdit = (item_id, item, description, e) => {
    e.preventDefault()
    this.setState({
      id: item_id,
      task: item,
      description: description,
      errorMessage: '',
      isUpdate: true,
      validation: this.validator.valid(),
    })
  }

  onDelete = (val, e) => {
    e.preventDefault()
    deleteItem(val).then((res) => {
      if (res.data.status === 'failed') {
        this.setState({ errorMessage: res.data.message })
      }
      this.getAll();
    }).catch(err => {
      this.setState({ errorMessage: err.data.message })
    })
  }

  startTime = () => {
    this.setState({
      timerOn: true,
      timerTime: this.state.timerTime,
      timerStart: Date.now() - this.state.timerTime
    });
    this.timer = setInterval(() => {
      this.setState({
        timerTime: Date.now() - this.state.timerStart
      });
    }, 10);
  };

  stopTime = () => {
    this.setState({ timerOn: false });
    clearInterval(this.timer);
  };

  render() {
    let centiseconds = ("0" + (Math.floor(timerTime / 10) % 100)).slice(-2);
    let seconds = ("0" + (Math.floor(timerTime / 1000) % 60)).slice(-2);
    let minutes = ("0" + (Math.floor(timerTime / 60000) % 60)).slice(-2);
    let hours = ("0" + Math.floor(timerTime / 3600000)).slice(-2);
    return (

      <div className="row">
        <div className="col-md-12 mt-5">
          <div className="col-md-12">
            {this.state.errorMessage !== '' ?
              <div className="alert alert-danger alert-dismissible fade show" role="alert">
                <strong>Error Message: </strong> {this.state.errorMessage}
              </div>
              :
              <div></div>
            }
          </div>
          <form onSubmit={this.onSubmit}>
            <div className="form-group">
              <label htmlFor="task">Task Title</label>
              <div className="row">
                <div className="col-md-12">
                  <input
                    type="text"
                    className="form-control"
                    value={this.state.task}
                    name="task"
                    onChange={this.onChange}
                  />
                  <span className="help-block">{this.state.validation.task.message}</span>
                </div>
              </div>
            </div>
            <div className="form-group">
              <label htmlFor="description">Task Description</label>
              <label htmlFor="task">Task Title</label>
              <div className="row">
                <div className="col-md-12">
                  <input
                    type="text"
                    className="form-control"
                    value={this.state.description}
                    name="description"
                    onChange={this.onChange}
                  />
                  <span className="help-block">{this.state.validation.description.message}</span>
                </div>
              </div>
            </div>
            <button className="btn btn-primary btn-block" onClick={this.onUpdate.bind(this)}
              style={this.state.isUpdate ? {} : { display: 'none' }}
            >
              Update
            </button>
            <button type="submit" onClick={this.onSubmit.bind(this)} className="btn btn-success btn-block"
              style={this.state.isUpdate ? { display: 'none' } : {}}
            >
              Submit
            </button>
            <button onClick={this.onCreate.bind(this)} className="btn btn-info btn-block"
              style={this.state.isUpdate ? {} : { display: 'none' }}
            >
              Close
            </button>
          </form>
          <table className="table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Description</th>
                <th>Created Date</th>
                <th>Time</th>
                <th className="text-right">Action</th>
              </tr>
            </thead>
            <tbody>
              {this.state.items.map((item, index) => (
                <tr key={index}>
                  <td className="text-left">{item.name}</td>
                  <td className="text-left">{item.description}</td>
                  <td className="text-left">{this.formatDate(new Date(item.createdAt))}</td>
                  <td className="text-left">{this.formatTime(new Date(item.timeTask))}</td>
                  <td className="text-left"><div className="Stoptimer">
                  <div className="Stoptimer-header">Stoptimer</div>
                        <div className="Stoptimer-display">
                               {hours} : {minutes} : {seconds}
                        </div>
                        {this.state.timerOn === false && (
                        <button onClick={this.startTime.bind(this)}>Start</button>
                        )}
                        {this.state.timerOn === true && (
                        <button onClick={this.stopTime.bind(this)}>Stop</button>
                        )}
                   </div></td>
                  <td className="text-right">
                    <button
                      className="btn btn-info mr-1"
                      disabled={this.state.editDisabled}
                      onClick={this.onEdit.bind(this, item.id, item.name, item.description)}
                    >
                      Edit
                    </button>
                    <button
                      href=""
                      className="btn btn-danger"
                      onClick={this.onDelete.bind(this, item.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    )
  }
}


export default TodoList
